DROP TABLE IF EXISTS `urls`;
CREATE TABLE `urls` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `uid` int(11) NOT NULL DEFAULT '0' COMMENT '用户id',
  `urlno` int(11) NOT NULL DEFAULT '10000' COMMENT '网址编号',
  `urlname` varchar(255) NOT NULL DEFAULT '' COMMENT '网站名称',
  `urlpath` varchar(255) NOT NULL DEFAULT '' COMMENT '网址',
  `status` smallint(6) NOT NULL DEFAULT '1001' COMMENT '1101表在线1001离线1011表停止',
  `meal` int(11) NOT NULL DEFAULT '0' COMMENT '套餐0表示免费',
  `frompath` varchar(2000) NOT NULL DEFAULT '' COMMENT '来源地址',
  `scale` smallint(6) NOT NULL DEFAULT '0' COMMENT '来源比例',
  `starttime` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '服务开始时间',
  `endtime` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '服务到期时间',
  `createtime` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '创建时间',
  `updatetime` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '更新时间',
  `firsttime` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '第一次跳转时间',
  `todaytimes` varchar(11) NOT NULL DEFAULT '0' COMMENT '今日跳转次数',
  `sumtimes` varchar(11) NOT NULL DEFAULT '0' COMMENT '总跳转次数',
  `unittimes` varchar(11) NOT NULL DEFAULT '0' COMMENT '单位时间跳转上限次数',
  `nowunittimes` varchar(6) NOT NULL DEFAULT '0' COMMENT '当前单位时间已跳转次数',
  `unitprice` varchar(5) NOT NULL DEFAULT '0.2' COMMENT '默认每100IP每天0.2元',
  `turntime` datetime DEFAULT '0000-00-00 00:00:00' COMMENT '记录最后一次跳转时间',
  PRIMARY KEY (`id`),
  KEY `urlnameindex` (`urlname`),
  KEY `urlpathindex` (`urlpath`)
) ENGINE=MyISAM AUTO_INCREMENT=767 DEFAULT CHARSET=utf8 COMMENT='网址表';

